// this page will explain what SaleSight offers to users.
// no need to be authenticated (unprotected route)
// renders LogInNavBar and key points.

// p tag or main to explain what SaleSight offers to users.
// 3 DIVS ALIGNED horizontally : Key Points; sale Insight, Profit Optimization and Real Time Revenue Management 


// box shadow, box inside of div and image . 
import React from "react";
import LogInNavBar from "../components/LogInNavBar";

// Import images for cards
import pie from "../assets/pie.png";
import profit from "../assets/profit.png";
import revenue from "../assets/revenue.png";


function About() {
  return (
    <>
      <LogInNavBar />
      {/*  flex-col stacks elements vertically */}
      <div className="flex flex-col justify-center bg-gray-100 min-h-screen pt-4">
        <div className="h-content-height flex-col flex items-center justify-center mb-8 transition-shadow duration-300 ease-in-out hover:shadow-red-900">
          <h1 className="text-3xl font-bold text-gray-800">
            Welcome to SaleSight - Your Revenue and Profit Management Tool
          </h1>
          <p className="text-gray-600 text-center text-xl w-full max-w-5xl mt-6 p-4">
            At SaleSight, we believe tracking your product sales and maximizing
            profits should be effortless and intuitive, with the power of
            real-time updates at your fingertips. Whether youâ€™re an e-commerce
            seller or a large enterprise, our platform is crafted to simplify
            your sales management and boost profit optimization, making growth
            easier than ever.
          </p>
        </div>

        {/* Key Points Section */}
        <div className="flex flex-col justify-center space-x-6 max-w-5xl mx-auto">
          {/* Card 1: Sale Insight */}
          <div className="bg-white rounded-lg shadow-lg p-6 flex flex-row items-center mb-8 transition-shadow duration-300 ease-in-out hover:shadow-red-900">
            <img src={pie} alt="Sale Insight" className="h-30 w-35" />
            <h2 className="text-2xl font-bold text-gray-800">Sale Insight</h2>
            <p className="text-gray-600 text-center mt-2 text-xl">
              Streamlines profit calculation for each product and visualizes
              cost and revenue with pies and charts.
            </p>
          </div>

          {/* Card 2: Profit Optimization */}
          <div className="bg-white rounded-lg shadow-lg p-6 flex flex-row items-center mb-8 transition-shadow duration-300 ease-in-out hover:shadow-red-900">
            <img src={profit} alt="Profit Optimization" className="h-20 mb-4" />
            <h2 className="text-2xl font-bold text-gray-800">
              Profit Optimization
            </h2>
            <p className="text-gray-600 text-center mt-2">
              Designed to calculate and track revenues, sales, costs, and
              profits for products sold by users.
            </p>
          </div>

          {/* Card 3: Real-Time Revenue Management */}
          <div className="bg-white rounded-lg shadow-lg p-6 flex flex-row items-center mb-8 transition-shadow duration-300 ease-in-out hover:shadow-red-900">
            <img
              src={revenue}
              alt="Real-Time Revenue Management"
              className="h-20 mb-4"
            />
            <h2 className="text-2xl font-bold text-gray-800">
              Real-Time Revenue Management
            </h2>
            <p className="text-gray-600 text-center mt-2">
              Continuously track and monitor changes in revenue, sales, and
              costs, providing immediate insights into business health.
            </p>
          </div>
        </div>
      </div>
    </>
  );
}

export default About;
