import React, { useState } from "react";
import LogInNavBar from "../components/LogInNavBar";
import { useFormik } from "formik";
import * as yup from "yup";

function Authentication() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const formClass = "bg-white p-6 rounded-lg shadow-lg w-full max-w-sm";
  const login = () => {
    console.log(username, password);
    // call login function here

  };


  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      {/* Navigation bar */}
      <LogInNavBar />

      {/* Centered form */}
      <main className="flex flex-col items-center w-full mt-8">
        <h1 className="text-2xl font-semibold mb-4">Please Login or Sign Up</h1>
        <form className={formClass} onSubmit={login}>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="username"
              type="text"
              name="username"
              placeholder="Please enter your username..."
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="mt-4">
            <label htmlFor="password">Password:</label>
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="password"
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button
            type="submit"
            className="mt-6 w-full bg-blue-500 text-white py-2 rounded-lg"
          >
            Submit
          </button>
        </form>
      </main>
    </div>
  );
}

export default Authentication;
