import React from "react";
import { createBrowserRouter, Navigate } from "react-router-dom";
import App from "./App";
import Authentication from "./pages/Authentication";
import ErrorPage from "./pages/ErrorPage";
import About from "./pages/About";
import ProtectedRoute from "./ProtectedRoute";
import AnalyticsPage from "./pages/AnalyticsPage";

// Create router configuration
const router = createBrowserRouter([
  {
    path: "/",
    element: <App />, // Main layout with Auth or child routes based on path
    errorElement: <ErrorPage />,
    children: [
      {
        path: "/",
        element: <Navigate to="/login" replace />, // Default route redirects to /login if unauthenticated
      },
      {
        path: "login",
        element: <Authentication />, // Login page
      },
      {
        path: "signup",
        element: <Authentication />, // Signup page
      },
      {
        path: "about",
        element: <About />,
      }
    ],
  },
  {
    path: "*", // Catch-all for unmatched routes
    element: <ErrorPage />,
  },
]);

export default router;
