// this page will explain what SaleSight offers to users.
// no need to be authenticated (unprotected route)
// renders LogInNavBar
// and About components

// welcome to SaleSight

// p tag or main to explain what SaleSight offers to users.
// 3 DIVS ALIGNED horizontally : Key Points; sale Insight, Profit Optimization and Real Time Revenue Management 


// box shadow, box inside of div and image . 
import React from "react";
import LogInNavBar from "../components/LogInNavBar";

// Import images for cards
import pie from "../assets/pie.png"; // Update with your actual image paths
// import ProfitOptimizationImage from "../assets/profit_optimization.png"; // Update with your actual image paths
// import RealTimeRevenueImage from "../assets/real_time_revenue.png"; // Update with your actual image paths

function About() {
  return (
    <>
      <LogInNavBar />
      <div className="flex flex-col items-center bg-gray-100 min-h-screen pt-4">
        <div className="h-content-height flex items-center justify-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800">
            Welcome to SaleSight - Your Revenue and Profit Management Tool
          </h1>
          <p className="text-gray-600 text-center text-lg max-w-xl">At SaleSight, we believe managing your product sales and profits should be intuitive, straightforward and easy to manage with real-time updates.  </p>
        </div>

        {/* Key Points Section */}
        <div className="flex flex-row justify-center space-x-6 max-w-5xl mx-auto">
          {/* Card 1: Sale Insight */}
          <div className="bg-white rounded-lg shadow-lg p-6 flex flex-col items-center">
            <img
              src={pie}
              alt="Sale Insight"
              className="h-45 mb-4"
            />
            <h2 className="text-xl font-semibold text-gray-800">
              Sale Insight
            </h2>
            <p className="text-gray-600 text-center mt-2">
              Streamlines profit calculation for each product and visualizes
              cost and revenue with pies and charts.
            </p>
          </div>

          {/* Card 2: Profit Optimization */}
          <div className="bg-white rounded-lg shadow-lg p-6 flex flex-col items-center">
            <img
              src={pie}
              alt="Profit Optimization"
              className="h-24 mb-4"
            />
            <h2 className="text-xl font-semibold text-gray-800">
              Profit Optimization
            </h2>
            <p className="text-gray-600 text-center mt-2">
              Designed to calculate and track revenues, sales, costs, and
              profits for products sold by users.
            </p>
          </div>

          {/* Card 3: Real-Time Revenue Management */}
          <div className="bg-white rounded-lg shadow-lg p-6 flex flex-col items-center">
            <img
              src={pie}
              alt="Real-Time Revenue Management"
              className="h-24 mb-4"
            />
            <h2 className="text-xl font-semibold text-gray-800">
              Real-Time Revenue Management
            </h2>
            <p className="text-gray-600 text-center mt-2">
              Continuously track and monitor changes in revenue, sales, and
              costs, providing immediate insights into business health.
            </p>
          </div>
        </div>
      </div>
    </>
  );
}

export default About;
