  return (
    <div>
        <div className="flex justify-center items-center min-h">
        {/* Save space for LogInNavBar */}
        <LogInNavBar />
          <main>
            <h1>Please Login or Sign Up</h1>
            <form className={formClass} onSubmit={handleSubmit}>
              <div>
                <label htmlFor="username">Username: </label>
