                  onChange={(e) => console.log(e.target.value)}
                  required
                />
              </div>
              <br />
              <div>
                <label htmlFor="password">Password: </label>
                <input
                  id="password"
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={"undefined"}
                  onChange={(e) => console.log(e.target.value)}
                  required
                />
              </div>
              <br />
              <button type="submit">Submit</button>
              <br />
            </form>
          </main>
        </div>
    </div>
  )
}


export default Authentication
