import React, { useState, useEffect } from "react";
import LogInNavBar from "../components/LogInNavBar";
import { useFormik } from "formik";
import * as yup from "yup";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { NavLink, useLocation, useNavigate } from "react-router-dom";
import { FaUser, FaLock } from "react-icons/fa"; // Import icons

function Authentication() {
  // import login function from AuthContext
  const { login, error, signup } = useContext(AuthContext);
  const location = useLocation();
  const navigate = useNavigate();

  // State to keep track of the current form type (login or signup)
  const [formType, setFormType] = useState(
    location.pathname === "/signup" ? "signup" : "login"
  );

  const initialValues =
    formType === "signup"
      ? { username: "", password: "", name: "", email: "" }
      : { username: "", password: "" };


  const validationSchema =
    formType === "signup"
      ? yup.object({
          username: yup.string().required("Username is required"),
          password: yup.string().required("Password is required"),
          name: yup.string().required("Name is required"),
          email: yup.string()
            .email("Invalid email")
            .required("Email is required"),
        })
      : yup.object({
          username: yup.string().required("Username is required"),
          password: yup.string().required("Password is required"),
        });


  const formik = useFormik({
    initialValues,
    validationSchema,
    onSubmit: (values) => {
      if (formType === "signup") {
        signup(values.username, values.password, values.name, values.email);
        formik.resetForm();
        navigate("/login");
      } else {
        login(values.username, values.password);
        formik.resetForm();
      }
    },
  });

  console.log(formik.values)

  // Redirect if user is authenticated
  // useEffect(() => {
  //   if (isAuthenticated) {
  //     navigate("/"); // or another authenticated route
  //   }
  // }, [isAuthenticated]);

  //clean up function if url changes to /signup.. clear form fields and errors messages
  useEffect(() => {
    if (location.pathname === "/signup") {
      formik.resetForm();
      setFormType("signup");
    } else {
      setFormType("login");
      formik.resetForm();
    }
  }, [location.pathname])



  // Styling classes
  const formClass =
    "bg-white p-6 rounded-lg shadow-lg w-full max-w-sm transition-shadow duration-300 ease-in-out hover:shadow-red-800";

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <LogInNavBar />
      <main className="flex flex-col items-center w-full mt-8">
        <h1 className="text-2xl font-semibold mb-4">
          {formType === "signup" ? "Sign Up" : "Login"}
        </h1>
        {error ? <p className="text-red-500 text-sm">{error}</p> : null}
        <form className={formClass} onSubmit={formik.handleSubmit}>
          <div className="relative mb-4">
            <label htmlFor="username">Username:</label>
            {/* add icon for username */}
            <FaUser className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" />
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="username"
              type="text"
              name="username"
              placeholder="Please enter your username..."
              value={formik.values.username}
              onChange={formik.handleChange}
            />
            {formik.touched.username && formik.errors.username ? (
              <div className="text-red-500 text-sm">
                {formik.errors.username}
              </div>
            ) : null}
          </div>
          {/* password input */}
          {/* positions icon relative to this div. bottom margin  */}
          <div className="relative mb-4"> 
            <label htmlFor="password">Password:</label>
            <FaLock />
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="password"
              type="password"
              name="password"
              placeholder="Password"
              value={formik.values.password}
              onChange={formik.handleChange}
            />
            {formik.touched.password && formik.errors.password ? (
              <div className="text-red-500 text-sm">
                {formik.errors.password}
              </div>
            ) : null}
          </div>
          {formType === "signup" && (
            <>
              <div className="mt-4">
                <label htmlFor="name">Full Name:</label>
                <input
                  className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
                  id="name"
                  type="text"
                  name="name"
                  placeholder="Please enter your name..."
                  value={formik.values.name}
                  onChange={formik.handleChange}
                />
                {formik.touched.name && formik.errors.name ? (
                  <div className="text-red-500 text-sm">
                    {formik.errors.name}
                  </div>
                ) : null}
              </div>
              <div className="mt-4">
                <label htmlFor="email">Email:</label>
                <input
                  className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
                  id="email"
                  type="email"
                  name="email"
                  placeholder="Please enter your email..."
                  value={formik.values.email}
                  onChange={formik.handleChange}
                />
                {formik.touched.email && formik.errors.email ? (
                  <div className="text-red-500 text-sm">
                    {formik.errors.email}
                  </div>
                ) : null}
              </div>
            </>
          )}
          <button
            type="submit"
            className="mt-6 w-full bg-gray-700 text-white py-2 rounded-lg"
          >
            Submit
          </button>
          <p>
            {formType === "signup"
              ? "Already have an account? "
              : "Don't have an account? "}
            <NavLink
              to={formType === "signup" ? "/login" : "/signup"}
              className="font-bold text-gray-500 hover:underline"
              onClick={() => {
                // Reset the form state when navigating between pages
                formik.resetForm();
                setFormType(formType === "signup" ? "login" : "signup");
              }}
            >
              {formType === "signup" ? "Login" : "Register"}
            </NavLink>
          </p>
        </form>
      </main>
    </div>
  );
}

export default Authentication;
