import React, { useState, useContext } from "react";
import LogInNavBar from "../components/LogInNavBar";
import { AuthContext } from "../context/AuthContext"; // Import your AuthContext

function Authentication() {
  const { currentUser, logIn, signUp } = useContext(AuthContext); // Get user state and auth functions from context
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isSignUp, setIsSignUp] = useState(false); // State to toggle between login and signup

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isSignUp) {
      signUp(username, password); // Call the signUp function
    } else {
      logIn(username, password); // Call the logIn function
    }
  };

  if (user) {
    return <h1>Welcome, {user.name}!</h1>; // Display welcome message if user is logged in
  }

  return (
    <div className="flex justify-center items-center min-h-screen">
      <LogInNavBar />
      <main className="bg-white p-6 rounded-lg shadow-lg w-full max-w-sm">
        <h1 className="text-center text-2xl mb-4">
          Please {isSignUp ? "Sign Up" : "Login"}
        </h1>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="username" className="block text-gray-700">
              Username:
            </label>
            <input
              id="username"
              type="text"
              name="username"
              placeholder="Please enter your username..."
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="password" className="block text-gray-700">
              Password:
            </label>
            <input
              id="password"
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-500 text-white p-2 rounded-lg"
          >
            {isSignUp ? "Sign Up" : "Login"}
          </button>
        </form>
        <button
          className="mt-4 text-blue-500 underline"
          onClick={() => setIsSignUp(!isSignUp)} // Toggle between login and signup
        >
          {isSignUp
            ? "Already have an account? Login"
            : "Don't have an account? Sign Up"}
        </button>
      </main>
    </div>
  );
}

export default Authentication;
