import React, { useState, useEffect } from "react";
import LogInNavBar from "../components/LogInNavBar";
import { useFormik } from "formik";
import * as yup from "yup";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { NavLink, useLocation, useNavigate } from "react-router-dom";

function Authentication() {
  // import login function from AuthContext
  const { login, error, signup } = useContext(AuthContext);
  const location = useLocation();
  const navigate = useNavigate();

  // State to keep track of the current form type (login or signup)
  const [formType, setFormType] = useState(
    location.pathname === "/signup" ? "signup" : "login"
  );

  console.log(location);

  // Yup validation schema
  const validationSchema = yup.object({
    username: yup
      .string()
      .required("Username is required")
      .max(60, "Must be less than 60 digits")
      .min(5, "Please enter a valid username")
      .transform((value) => {
      //if value is not null, then convert it to upper case
      return value !== null ? value.toUpperCase() : value;
    }),
    password: yup.string().required("Password is required"),
    name: yup.string().required("Name is required"),
    email: yup
      .string()
      .email("Invalid email address")
      .required("Email is required"),
  });

  // Formik setup
  const formik = useFormik({
    initialValues: {
      username: "",
      password: "",
      name: "",
      email: "",
    },
    validationSchema,
    onSubmit: (values) => {
      console.log(values);
      // if signup , send all form data
      if (formType === 'signup') {
        signup(values.username, values.password, values.name, values.email);
        // reset form state
        formik.resetForm();
      } else {
        login(values.username, values.password);
        formik.resetForm();
      }
    },
  });
  console.log(formType)

  // Reset the form state when the URL changes
  useEffect(() => {
    setFormType(location.pathname === "/signup" ? "signup" : "login");
  }, [location.pathname]);

  // Styling classes
  const formClass = "bg-white p-6 rounded-lg shadow-lg w-full max-w-sm";

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <LogInNavBar />
      <main className="flex flex-col items-center w-full mt-8">
        <h1 className="text-2xl font-semibold mb-4">
          {formType === "signup" ? "Sign Up" : "Login"}
        </h1>
        {error ? <p className="text-red-500 text-sm">{error}</p> : null}
        <form className={formClass} onSubmit={formik.handleSubmit}>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="username"
              type="text"
              name="username"
              placeholder="Please enter your username..."
              value={formik.values.username}
              onChange={formik.handleChange}
            />
            {formik.touched.username && formik.errors.username ? (
              <div className="text-red-500 text-sm">
                {formik.errors.username}
              </div>
            ) : null}
          </div>
          <div className="mt-4">
            <label htmlFor="password">Password:</label>
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="password"
              type="password"
              name="password"
              placeholder="Password"
              value={formik.values.password}
              onChange={formik.handleChange}
            />
            {formik.touched.password && formik.errors.password ? (
              <div className="text-red-500 text-sm">
                {formik.errors.password}
              </div>
            ) : null}
          </div>
          {formType === "signup" && (
            <>
              <div className="mt-4">
                <label htmlFor="name">Full Name:</label>
                <input
                  className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
                  id="name"
                  type="text"
                  name="name"
                  placeholder="Please enter your name..."
                  value={formik.values.name}
                  onChange={formik.handleChange}
                />
                {formik.touched.name && formik.errors.name ? (
                  <div className="text-red-500 text-sm">
                    {formik.errors.name}
                  </div>
                ) : null}
              </div>
              <div className="mt-4">
                <label htmlFor="email">Email:</label>
                <input
                  className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
                  id="email"
                  type="email"
                  name="email"
                  placeholder="Please enter your email..."
                  value={formik.values.email}
                  onChange={formik.handleChange}
                />
                {formik.touched.email && formik.errors.email ? (
                  <div className="text-red-500 text-sm">
                    {formik.errors.email}
                  </div>
                ) : null}
              </div>
            </>
          )}
          <button
            type="submit"
            className="mt-6 w-full bg-gray-700 text-white py-2 rounded-lg"
          >
            Submit
          </button>
          <p>
            {formType === "signup"
              ? "Already have an account? "
              : "Don't have an account? "}
            <NavLink
              to={formType === "signup" ? "/login" : "/signup"}
              className="font-bold text-gray-500 hover:underline"
              onClick={() => {
                // Reset the form state when navigating between pages
                formik.resetForm();
                setFormType(formType === "signup" ? "login" : "signup");
              }}
            >
              {formType === "signup" ? "Login" : "Register"}
            </NavLink>
          </p>
        </form>
      </main>
    </div>
  );
}

export default Authentication;
