import React from "react";
// Outlet to render child components
import { Outlet, useLocation } from "react-router-dom";
// Import Authentication component
import Authentication from "./pages/Authentication.js";

function App() {
  const location = useLocation();

  // if location.pathname is /login, signup show Authentication component or render other child components.
  //

  return (
    <>
      {/* Render Authentication component only for /login and /signup */}
      {location.pathname === "/login" || location.pathname === "/signup" ? (
        <Authentication />
      ) : (
        <Outlet /> // Render child routes (e.g., /about, /analytics) if not on login/signup routes
      )}
    </>
  );
}

export default App;
