import React from "react";
import LogInNavBar from "../components/LogInNavBar";
import { useFormik } from "formik";
import * as yup from "yup";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

function Authentication() {

  // import login function from AuthContext
  const { login, error } = useContext(AuthContext)

  // Yup validation schema
  const validationSchema = yup.object({
    username: yup.string().required("Username is required")
    .max(60, "Must be less than 60 digits")
    .min(5, "Please enter a valid username"),
    password: yup.string().required("Password is required"),
  });

  // Formik setup
  const formik = useFormik({
    initialValues: {
      username: "",
      password: "",
    },
    validationSchema,
    onSubmit: (values) => {
      console.log(values);
      // Add login function here to handle form submission
      login(values.username, values.password);
    },
  });

  console.log(formik.values.username);
  console.log(formik.values.password);

  // Styling classes
  const formClass = "bg-white p-6 rounded-lg shadow-lg w-full max-w-sm";

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <LogInNavBar />
      <main className="flex flex-col items-center w-full mt-8">
        <h1 className="text-2xl font-semibold mb-4">Please Login or Sign Up</h1>
        <form className={formClass} onSubmit={formik.handleSubmit}>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="username"
              type="text"
              name="username"
              placeholder="Please enter your username..."
              value={formik.values.username}
              onChange={formik.handleChange}
            />
            {formik.touched.username && formik.errors.username ? (
              <div className="text-red-500 text-sm">
                {formik.errors.username}
              </div>
            ) : null}
          </div>
          <div className="mt-4">
            <label htmlFor="password">Password:</label>
            <input
              className="bg-gray-50 border border-blue-300 text-blue-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-72 p-2.5 mt-2"
              id="password"
              type="password"
              name="password"
              placeholder="Password"
              value={formik.values.password}
              onChange={formik.handleChange}
            />
            {formik.touched.password && formik.errors.password ? (
              <div className="text-red-500 text-sm">
                {formik.errors.password}
              </div>
            ) : null}
          </div>
          <button
            type="submit"
            className="mt-6 w-full bg-gray-700 text-white py-2 rounded-lg"
          >
            Submit
          </button>
          {error ? <p className="text-red-500 text-sm">{error}</p> : null}
        </form>
      </main>
    </div>
  );
}

export default Authentication;
